(112, '_transient_feed_4c74a77fb0311cda9aef46304170e010', 'a:4:{s:5:"child";a:1:{s:0:"";a:1:{s:3:"rss";a:1:{i:0;a:6:{s:4:"data";s:3:"\n	\n";s:7:"attribs";a:1:{s:0:"";a:1:{s:7:"version";s:3:"2.0";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:0:"";a:1:{s:7:"channel";a:1:{i:0;a:6:{s:4:"data";s:132:"\n		\n		\n		\n		\n		\n		\n				\n\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n\n	";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:25:"日本語 » Recent Posts";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:31:"http://ja.forums.wordpress.org/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:15:"フォーラム";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"language";a:1:{i:0;a:5:{s:4:"data";s:2:"ja";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 06:50:54 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:9:"generator";a:1:{i:0;a:5:{s:4:"data";s:25:"http://bbpress.org/?v=1.1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"item";a:35:{i:0;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:89:"ooshirotakashi :  &quot;Buddypress Portfolioの並び順をランダムにしたい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120916#post-168466";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 06:49:41 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168466@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1038:"<p>Wordpress及びプログラミング言語を学んだことがない初心者です。<br />\n1年程前からWordpressでウェブサイト作りをしています。</p>\n<p>現在Buddypressとマルチサイトを併用したサイトを作っています。</p>\n<p>Buddypress Portfolioというプラグインを使用しており、ランダムに表示をしたいのですがソートの仕方が分かりません。</p>\n<p>そもそもorderbyの様なオプションが無いようです。</p>\n<p>関数等を作成してどうにかランダム表示を実現したいのですが、自力でPHPが書けないため分かりません。</p>\n<p>探しても情報がないため、どなたかご教示お願いします&#62;&#60;</p>\n<p>Buddypress Portfolioの関数は以下で見ることができます。<br />\n<a href="https://github.com/ncrocfer/BP-Portfolio/blob/master/includes/bp-portfolio-template.php" rel="nofollow">https://github.com/ncrocfer/BP-Portfolio/blob/master/includes/bp-portfolio-template.php</a>\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:14:"ooshirotakashi";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:1;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:117:"stranger-jp :  &quot;ダッシュボードにアクセスすると投稿一覧へリダイレクトさせたい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120906#post-168463";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 06:44:52 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168463@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:393:"<p>色々と方法があると思いますが、Peter''s Login Redirectプラグインを使うと<br />\n色々な運用方法が可能かと思います。<br />\n<a href="http://wordpress.org/plugins/peters-login-redirect/">http://wordpress.org/plugins/peters-login-redirect/</a><br />\n任意のユーザーのみ、または任意のユーザー権限のみ等の指定が可能です。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:11:"stranger-jp";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:2;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:88:"kuma69kuma :  &quot;固定ページでpagenaviのページングが機能しない&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120910#post-168459";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 06:34:13 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168459@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1570:"<p>はじめて投稿させていただきます。<br />\nワードプレスは最近使い始めたために、勉強不足な面があると思いますが、どうかご教示ください。</p>\n<p>今、特定のカテゴリーの記事だけを表示する固定ページを作成しております。<br />\nカテゴリー記事を１ページに5記事表示するようにし、残りの記事はページングで移動できるようにしたいのですが、うまく反映しません。<br />\nページングの表示は出るのですが、２や３をクリックしても1ページ目の記事が表示され、ページングの表示も1ページのままです。</p>\n<p>同じ内容のフォーラムやブログなどを拝見し、いろいろ試してみましたが改善がありません。<br />\nもしよろしければ、どなたかご教示願えませんでしょうか？<br />\nよろしくお願い致します。</p>\n<p>以下が使用しているコードです。</p>\n<pre><code>&#60;?php query_posts(&#039;category_name=slug_clean&#38;posts_per_page=5&#38;paged=&#039;.get_query_var(&#039;paged&#039;) ); ?&#62;\n\n&#60;?php if (have_posts()) : ?&#62;\n&#60;?php while (have_posts()) : the_post(); ?&#62;\n\n&#60;div class=&#34;entryBox&#34;&#62;\n\n&#60;p&#62;&#60;h2&#62;&#60;?php the_title(); ?&#62;&#60;/h2&#62;&#60;/p&#62;\n&#60;?php the_content(); ?&#62;\n&#60;/div&#62;\n\n&#60;?php endwhile; ?&#62;\n\n&#60;?php if(function_exists(&#039;wp_pagenavi&#039;)) { wp_pagenavi(); } ?&#62;\n&#60;?php wp_reset_query();endif; ?&#62;</code></pre>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"kuma69kuma";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:3;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:114:"makoyano :  &quot;ダッシュボードにアクセスすると投稿一覧へリダイレクトさせたい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120906#post-168455";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 05:54:58 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168455@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:531:"<p>管理画面で「ダッシュボード」にアクセスする、もしくはログイン画面からログインすると「投稿一覧」ページへ移動したいと思っていますが、実現方法を調べてみたのですが、なかなか答えが見つからず、こちらに投稿させていただきました。<br />\nWordPressは常に最新にしておきたいのでコアファイルをいじるのはNGという条件ですが、実現可能でしょうか。<br />\nよろしくお願いします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:8:"makoyano";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:4;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:96:"og112 :  &quot;カテゴリーをメニューに表示出来たがCSSが適用されない&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120896#post-168452";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 05:42:01 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168452@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:173:"<p>返信ありがとうございます。現在のメニューを消して、カスタムメニューで新たなメニューを作る事で解決できそうです。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:5:"og112";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:5;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:92:"mel_cha :  &quot;Custom Post Type Permalinksのタームを使ったパーマリンク&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120901#post-168449";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 05:33:44 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168449@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1986:"<p>お世話になります。</p>\n<p>カスタム投稿タイプのURLで<br />\n<a href="http://ドメイン/カスタム投稿タイプ名/ターム/スラッグ/" rel="nofollow">http://ドメイン/カスタム投稿タイプ名/ターム/スラッグ/</a><br />\nの形式のURLにしたく、管理画面上で</p>\n<p>/%cus_sample（タクソノミー）%/%postname%/</p>\n<p>にしたところ、URL自体は反映されたのですが、single-カスタム投稿.phpが適用されず、<br />\nindex.phpが読み込まれてしまいます。<br />\nこれをきちんとsingle-カスタム投稿.phpを読み込ませる方法を助言いただけないでしょうか？</p>\n<p>書いているregister_post_typeの設定は下記の通りです。</p>\n<pre><code>register_post_type ( &#039;sample&#039;, array(\n	&#039;label&#039; =&#62; &#039;カスタム投稿&#039;,\n	&#039;labels&#039; =&#62; array(\n		&#039;name&#039; =&#62; &#039;カスタム投稿&#039;,\n		&#039;singular_name&#039; =&#62; &#039;カスタム投稿&#039;,\n		&#039;add_new_item&#039; =&#62; &#039;記事を追加&#039;,\n		&#039;new_item&#039; =&#62; &#039;記事&#039;,\n		&#039;view_item&#039; =&#62; &#039;記事を表示&#039;,\n		&#039;not_found&#039; =&#62; &#039;記事は見つかりませんでした&#039;,\n		&#039;not_found_in_trash&#039; =&#62; &#039;ゴミ箱に記事はありません。&#039;,\n		&#039;search_items&#039; =&#62; &#039;記事を検索&#039;\n	),\n	&#039;has_archive&#039; =&#62; true,\n	&#039;public&#039; =&#62; true,\n	&#039;show_ui&#039; =&#62; true,\n	&#039;taxonomies&#039; =&#62; &#039;cus_sample&#039;,\n	&#039;query_var&#039; =&#62; true,\n	&#039;rewrite&#039; =&#62; true,\n	&#039;hierarchical&#039; =&#62; false,\n	&#039;exclude_from_search&#039; =&#62; false,\n	&#039;menu_position&#039; =&#62; 5,\n	&#039;supports&#039; =&#62; array(\n		&#039;title&#039;,&#039;editor&#039;,&#039;excerpt&#039;,\n		&#039;revisions&#039;,&#039;thumbnail&#039;,&#039;author&#039;\n	),\n) );</code></pre>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:7:"mel_cha";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:6;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:99:"makoyano :  &quot;全部の子サイトの固定ページをメインサイトに一覧表示&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:54:"http://ja.forums.wordpress.org/topic/35503#post-168448";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 05:33:02 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168448@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:635:"<p>すいません。投稿が無かったもので、別方法は無いのかと思って放置していました。</p>\n<p>なるほど、そういう方法もあるんですね。<br />\n初めて知りました。<br />\n実現しようと思っていたものは、即時反映することが望ましいため、キャッシュだと差異が出てしまいそうで使えませんが、どこか別で使ってみたい機能です。</p>\n<p>とりあえず、一旦全部取得してソートするやり方で実現はできましたので、解決済みとさせていただきます。ありがとうございました。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:8:"makoyano";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:7;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:93:"satoh_msys :  &quot;会員管理機能プラグイン「Wishlist Members」について&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120900#post-168447";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 05:31:03 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168447@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:475:"<p><a href="http://member.wishlistproducts.com/#faq" rel="nofollow">http://member.wishlistproducts.com/#faq</a></p>\n<p>「Wishlist Member」をお使いの方はいらっしゃいますでしょうか？</p>\n<p>こちら、未ログインのユーザーに対し、トップページへのアクセスをブロックしログインを要求する機能はないのでしょうか？<br />\n下層コンテンツには個々のページから設定できるのですが…\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"satoh_msys";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:8;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:102:"stranger-jp :  &quot;カテゴリーをメニューに表示出来たがCSSが適用されない&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120896#post-168446";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 05:26:26 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168446@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:651:"<p>ここでサポート可能なのは基本的にWordPress公式のものに限ります。<br />\nお使いのテーマはWordPress公式のものではありませんので、<br />\nテーマの配布先にお尋ねされることをお勧めします。<br />\n尚、お使いのテーマはArtisteer 2.3.0.23326で生成されたもののようですが、<br />\nArtisteer 2.3.0.23326は4年も前に公開されたアプリケーションですので、<br />\nブラウザで云うならば腐った牛乳に使いものがあります。<br />\n腐った牛乳または腐りかけの牛乳には手を出さない事をお勧めします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:11:"stranger-jp";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:9;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:61:"Takuro Hishikawa :  &quot;WP-PageNavi　の記述場所&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120060#post-168445";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 05:23:19 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168445@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:458:"<p>ちなみに、ページ送りを表示したとして、そのリンクをクリックすると、ウェルカムとトップ表示にも影響を与えますので、そちらも修正が必要になるでしょう。これは、複数のページ送りを同一画面に同居できないというWordPressの設計が原因です。テンプレートごと捨てる必要はありませんが、全体的に手を入れる必要があります。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:16:"Takuro Hishikawa";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:10;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:94:"lonlypop :  &quot;WordPressのfeedのURLにアクセスしても何も表示されない&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120315#post-168444";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 05:16:19 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168444@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:303:"<p>gatespace様</p>\n<p>ご回答ありがとうございます。<br />\n&#60;?php bloginfo(''rss2_url''); ?&#62;をheder.phpに追加し、feedのURLが表示されましたが、質問時と同じhttp://hogehoge.com/feed/というURLになり、中には何も含まれておりませんでした。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:8:"lonlypop";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:11;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:54:"yoshi2012 :  &quot;WP-PageNavi　の記述場所&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120060#post-168443";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 05:06:26 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168443@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:263:"<p>あああ・・・そういうことですか（泣）<br />\nこのテンプレートを捨てないといけないってことなので<br />\nそこまでするかどうか悩みます・・・。<br />\n（私はコピペでしかPHPをいじれない）\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:9:"yoshi2012";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:12;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:86:"nobita :  &quot;raindrops 自動挿入されるp、brタグを削除する方法&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120362#post-168442";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 05:05:07 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168442@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1120:"<p>すみません　代替案　コード間違えました</p>\n<p>本文に書いたp要素も消えるので、</p>\n<p>functions.phpの先頭(<code>&#60;?php</code>の前）に追加<br />\n<pre><code>&#60;?php\nadd_filter( &#039;the_content&#039;, &#039;wpautop2&#039;, 5 );\nadd_filter( &#039;the_content&#039;, &#039;wpautop3&#039;,11 );\n\nfunction wpautop2( $content ) {\n\n    if ( is_page() ) {\n\n        return str_replace( array(&#039;&#60;p&#62;&#039;, &#039;&#60;/p&#62;&#039;,&#34;\\n&#34;,),\n                            array(&#039;&#60;p class=&#34;paragraph&#34;&#62;&#039;,&#039;&#60;/p class=&#34;paragraph&#34;&#62;&#039;, &#039;&#039; ),\n                            $content );\n    }\n}\n\nfunction wpautop3( $content ) {\n    if ( is_page() ) {\n        return str_replace( array( &#34;&#60;p&#62;&#34;, &#34;&#60;/p&#62;&#34; , &#039;&#60;p class=&#34;paragraph&#34;&#62;&#039;,&#039;&#60;/p class=&#34;paragraph&#34;&#62;&#039;),\n                            array( &#039;&#039;,&#039;&#039;,&#039;&#60;p&#62;&#039;,&#039;&#60;/p&#62;&#039;),\n                            $content );\n    }\n}\n?&#62;</code></pre>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"nobita";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:13;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:61:"Takuro Hishikawa :  &quot;WP-PageNavi　の記述場所&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120060#post-168441";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 04:56:05 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168441@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:217:"<p>テンプレートレベルの話ですね。私が言いたいのは、新着記事の表示にget_postsを使うのをやめて、WP_Queryを使えば解決しますよ、というアドヴァイスです。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:16:"Takuro Hishikawa";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:14;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:100:"dive_kei :  &quot;Xserverでメディアからファイルのアップロードが出来ない&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/119597#post-168440";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 04:55:46 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168440@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1864:"<p>お世話になっております。</p>\n<p>今だお客様側より アップロードが出来ない状況が続いております。</p>\n<p>mypacecreator 様から頂きました<br />\nerror_log、access_log の確認を行いました。</p>\n<p>error_log には、何もありませんでしたが、<br />\naccess_log に、お客様側からのアクセスで以下の気になるログがありました。<br />\n※サイトは、.htaccess でベーシック認証をかけています。</p>\n<p>*.*.*.* - - [29/Apr/2014:11:09:22 +0900] "POST /wp/wp-admin/async-upload.php HTTP/1.1" 400 - "http://*****.xsrv.jp/wp/wp-admin/media-new.php"<br />\n*.*.*.* - - [29/Apr/2014:10:17:33 +0900] "POST /wp/wp-admin/async-upload.php HTTP/1.1" 500 2553 "http://*****.xsrv.jp/wp/wp-admin/media-new.php"<br />\n*.*.*.* - - [29/Apr/2014:10:17:19 +0900] "POST /wp/wp-admin/async-upload.php HTTP/1.1" 500 2553 "http://*****.xsrv.jp/wp/wp-admin/media-new.php"<br />\n*.*.*.* - - [29/Apr/2014:10:46:46 +0900] "POST /wp/wp-admin/async-upload.php HTTP/1.1" 70007 2553 "http://*****.xsrv.jp/wp/wp-admin/media-new.php"</p>\n<p>試に自分の環境でアクセスすると下記になります。</p>\n<p>*.*.*.* - demo [30/Apr/2014:11:31:01 +0900] "POST /wp/wp-admin/async-upload.php HTTP/1.1" 200 2 "http://*****.xsrv.jp/wp/wp-admin/media-new.php"<br />\n*.*.*.* - demo [30/Apr/2014:11:31:02 +0900] "POST /wp/wp-admin/async-upload.php HTTP/1.1" 200 323 "http://*****.xsrv.jp/wp/wp-admin/media-new.php"</p>\n<p>お客様の場合、POSTの場合だけ認証外で接続しに来ているためアップが出来ない気がしますが、本サイト側は、認証はかけて無いので原因は別にあるかもしれません。</p>\n<p>解決方法をご存じの方がおられましたら<br />\nご教授頂きたく思いますのでよろしくお願い致します。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:8:"dive_kei";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:15;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:54:"yoshi2012 :  &quot;WP-PageNavi　の記述場所&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120060#post-168437";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 04:40:57 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168437@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:374:"<p>新着表示＝get_posts()ではないのでしょうか？</p>\n<p>競合するなら使わなければいいかなと思ったのですが・・・</p>\n<p>これはテンプレートレベルの話ですか？<br />\n（つまり、私が「wp_pagenavi」を使いたいならテンプレートを変えるのが一番はやいという意味でしょうか？　）\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:9:"yoshi2012";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:16;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:61:"Takuro Hishikawa :  &quot;WP-PageNavi　の記述場所&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120060#post-168436";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 04:36:52 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168436@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:115:"<p>新着表示を捨てて、wp_pagenaviを導入したい、とは、どういうことでしょうか…。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:16:"Takuro Hishikawa";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:17;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:100:"gatespace :  &quot;固定ページの記事一覧で同じ投稿者が表示されてしまう&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120880#post-168435";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 04:35:14 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168435@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:614:"<p>ざっと見ただけですが、 get_posts の使い方（ループ部分）が間違っています。<br />\n<a href="http://wpdocs.sourceforge.jp/%E3%83%86%E3%83%B3%E3%83%97%E3%83%AC%E3%83%BC%E3%83%88%E3%82%BF%E3%82%B0/get_posts" rel="nofollow">http://wpdocs.sourceforge.jp/%E3%83%86%E3%83%B3%E3%83%97%E3%83%AC%E3%83%BC%E3%83%88%E3%82%BF%E3%82%B0/get_posts</a></p>\n<p>基本は<br />\n<pre><code>&#60;?php\n$args = array( );\n$myposts = get_posts( $args );\nforeach ( $myposts as $post ) : setup_postdata( $post ); // これ大事\n// 各投稿\nendforeach;\nwp_reset_postdata(); // これ大事\n?&#62;</code></pre>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:9:"gatespace";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:18;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:54:"yoshi2012 :  &quot;WP-PageNavi　の記述場所&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120060#post-168434";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 04:34:31 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168434@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:449:"<p>ご回答有難うございます。</p>\n<p>＞get_posts()とwp_pagenavi()を連携させることはできません。<br />\n連携させたいという意図はありません。</p>\n<p>話から想像するに「wp_pagenavi」を使うということは<br />\n連携させるという意味になる可能性があると感じております。</p>\n<p>そうなると新着表示を捨てて、「wp_pagenavi」を導入したいです。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:9:"yoshi2012";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:19;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:61:"Takuro Hishikawa :  &quot;WP-PageNavi　の記述場所&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120060#post-168433";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 04:26:41 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168433@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:698:"<p>競合の問題ではありません。<code>get_posts()</code>と<code>wp_pagenavi()</code>を連携させることはできません。その理由は、<code>wp_pagenavi()</code>は内部的にメインクエリーを参照しており、サブクエリーを参照するには、引数で明示的に渡してあげる必要があるのです。</p>\n<p>私が書いたコードは、WP_Queryのパラメーターを設定していませんので、そのままでは動きません。詳細はCodexの<a href="http://wpdocs.sourceforge.jp/%E9%96%A2%E6%95%B0%E3%83%AA%E3%83%95%E3%82%A1%E3%83%AC%E3%83%B3%E3%82%B9/WP_Query">関数リファレンス/WP_Query</a>をお読みください。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:16:"Takuro Hishikawa";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:20;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:96:"og112 :  &quot;カテゴリーをメニューに表示出来たがCSSが適用されない&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120896#post-168431";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 04:19:45 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168431@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1757:"<p>いろいろ試してみましたが、うまくいかずハマってしまったので質問させてください。</p>\n<p>私の使っているテンプレートはカスタムメニューが適用されず、<br />\n固定ページを追加すると、グローバルメニューに固定ページリストが自動的に出ます。</p>\n<p>このグローバルメニューに固定ページだけでなく、カテゴリーも表示したいと思い、<br />\nfunctions.php内にあるpagesのタグをコピーし、そこを''cats''に置き換えたものを追記した所、ひとまずカテゴリーもメニュー内に表示されるようにはなりました。</p>\n<p>しかし、なぜかカテゴリー部分だけ背景が適用されずにいます。</p>\n<p>ちなみにそのサイトはここです<br />\n<a href="http://eibunpo.ciao.jp/wp1/" rel="nofollow">http://eibunpo.ciao.jp/wp1/</a></p>\n<p>ページソースを見ると、メニュー背景が適用されている文字には<br />\n<code>&#60;span class=&#34;l&#34;&#62;&#60;span class=&#34;r&#34;&#62;&#60;span class=&#34;t&#34;&#62;</code><br />\nという３つが付いてますが、私が新たに追記したカテゴリーにはこれらが付いていません。<br />\nただ、この３つをどうやってそのカテゴリーに適用すればいいのかが分かりません。<br />\nheader.phpやfunction.phpを見ても、それに該当する部分が見つからず困っています。<br />\n適用出来れば固定ページリスト同様に背景が付くと思うのですが。</p>\n<p>ちなみに使っているテンプレートはbusiness_01（broval）<br />\nというものです。</p>\n<p>どなたかご教授頂ければ幸いです。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:5:"og112";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:21;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:54:"yoshi2012 :  &quot;WP-PageNavi　の記述場所&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120060#post-168427";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 04:11:04 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168427@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1323:"<p>上記で頂いたコードを<br />\n&#60;!-- end_新着記事一覧 --&#62;の前に貼りましたが<br />\nサイトが崩壊してしまいましたので戻しました。</p>\n<p>＞新着記事一覧の最下部に入れたいです。<br />\nこれは理想で、wp_pagenaviにこだわっているわけでもありません。<br />\nwordpressの欠点であるページ送りができないという欠点を回避したいだけです。</p>\n<p>＞get_posts()を使うと、wp_pagenavi()を記述しても、get_posts()で表示している一覧についてのページ送りになりません。<br />\n競合するみたいなものと理解しています。</p>\n<p>＞wp_pagenavi()の引数queryにWP_Queryのインスタンスを渡す必要があります。<br />\nここが不明ですが、解説をしてもらっても理解するには私の予備知識がなさすぎます。<br />\n（VBAくらいしか言語？をしらないので）</p>\n<p>もしかして、<br />\n&#60;h3&#62;新着情報&#60;/h3&#62;</p>\n<p>&#60;!-- end_新着記事一覧 --&#62;<br />\nこの間の記述って不要ですか？<br />\nウィジェットの新着記事一覧」を使っているのなら<br />\nこの記述は不要になるから、競合の問題は解決するように思えるのですが・・・\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:9:"yoshi2012";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:22;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:106:"massaa :  &quot;カテゴリー、子カテゴリーのサイドバーをカスタマイズしたい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120887#post-168421";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 02:58:52 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168421@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1299:"<p>カテゴリーページ及び子カテゴリーページへ遷移したときのサイドバーのカスタマイズについてお教えいただけないでしょうか？</p>\n<p>親カテゴリーが車だとします。</p>\n<p>車の親カテゴリーページのサイドバー表示<br />\n車<br />\n　-子カテゴリー１<br />\n　-子カテゴリー２<br />\n　-子カテゴリー３</p>\n<p>そして子カテゴリー１に遷移したときのサイドバー<br />\n車<br />\n　-子カテゴリー１<br />\n　　-記事タイトル１<br />\n　　-記事タイトル２<br />\n　　-記事タイトル３<br />\n　-子カテゴリー２<br />\n　-子カテゴリー３</p>\n<p>子カテゴリー２に遷移したときのサイドバー<br />\n車<br />\n　-子カテゴリー１<br />\n　-子カテゴリー２<br />\n　　-記事タイトル１<br />\n　　-記事タイトル２<br />\n　　-記事タイトル３<br />\n　-子カテゴリー３</p>\n<p>親カテゴリーの時は子カテゴリー一覧表示、<br />\n子カテゴリーに遷移したときはそれに属する子カテゴリーの記事タイトルを表示させたいのですが</p>\n<p>サイドバーに記述する方法をご存知の方いらっしゃいましたらお教えくださいませ。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"massaa";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:23;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:85:"ibis7788 :  &quot;get_posts を含めたコードをもっと簡潔にしたい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120062#post-168416";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 02:29:30 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168416@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:7583:"<p>gogoweb様・uji Yamabata様、先日はご返答ありがとうございました。<br />\nいただいた案を元にして、以下のようにしてみました。</p>\n<pre><code>&#60;?php\n$listPref =array(\n	&#34;01&#34; =&#62; array( &#34;tag&#34; =&#62; &#34;hokkaido&#34;, &#34;name&#34; =&#62; &#34;北海道&#34;,\n			&#34;pref&#34; =&#62; array(\n				&#34;0101&#34; =&#62; &#34;北海道&#34;\n			)\n		),\n	&#34;02&#34; =&#62; array( &#34;tag&#34; =&#62; &#34;touhoku&#34;, &#34;name&#34; =&#62; &#34;東北&#34;,\n			&#34;pref&#34; =&#62; array(\n				&#34;0201&#34; =&#62; &#34;青森県&#34;, &#34;0202&#34; =&#62; &#34;岩手県&#34;, &#34;0203&#34; =&#62; &#34;宮城県&#34;, &#34;0204&#34; =&#62; &#34;秋田県&#34;, &#34;0205&#34; =&#62; &#34;山形県&#34;, &#34;0206&#34; =&#62; &#34;福島県&#34;\n			)\n		),\n	&#34;03&#34; =&#62; array( &#34;tag&#34; =&#62; &#34;kanto&#34;, &#34;name&#34; =&#62; &#34;関東地方&#34;,\n			&#34;pref&#34; =&#62; array(\n				&#34;0301&#34; =&#62; &#34;茨城県&#34;, &#34;0302&#34; =&#62; &#34;栃木県&#34;, &#34;0303&#34; =&#62; &#34;群馬県&#34;, &#34;0304&#34; =&#62; &#34;埼玉県&#34;, &#34;0305&#34; =&#62; &#34;千葉県&#34;, &#34;0306&#34; =&#62; &#34;東京都&#34;, &#34;0307&#34; =&#62; &#34;神奈川県&#34;\n			)\n		),\n	&#34;04&#34; =&#62; array( &#34;tag&#34; =&#62; &#34;chubu&#34;, &#34;name&#34; =&#62; &#34;中部地方&#34;,\n			&#34;pref&#34; =&#62; array(\n				&#34;0401&#34; =&#62; &#34;新潟県&#34;, &#34;0402&#34; =&#62; &#34;富山県&#34;, &#34;0403&#34; =&#62; &#34;石川県&#34;, &#34;0404&#34; =&#62; &#34;福井県&#34;, &#34;0405&#34; =&#62; &#34;山梨県&#34;, &#34;0406&#34; =&#62; &#34;長野県&#34;, &#34;0407&#34; =&#62; &#34;岐阜県&#34;, &#34;0408&#34; =&#62; &#34;静岡県&#34;, &#34;0409&#34; =&#62; &#34;愛知県&#34;, &#34;0410&#34; =&#62; &#34;三重県&#34;\n			)\n		),\n	&#34;05&#34; =&#62; array( &#34;tag&#34; =&#62; &#34;kinki&#34;, &#34;name&#34; =&#62; &#34;近畿地方&#34;,\n			&#34;pref&#34; =&#62; array(\n				&#34;0501&#34; =&#62; &#34;滋賀県&#34;, &#34;0502&#34; =&#62; &#34;京都府&#34;, &#34;0503&#34; =&#62; &#34;大阪府&#34;, &#34;0504&#34; =&#62; &#34;兵庫県&#34;, &#34;0505&#34; =&#62; &#34;奈良県&#34;, &#34;0506&#34; =&#62; &#34;和歌山県&#34;\n			)\n		),\n	&#34;06&#34; =&#62; array( &#34;tag&#34; =&#62; &#34;chugoku&#34;, &#34;name&#34; =&#62; &#34;中国・四国地方&#34;,\n			&#34;pref&#34; =&#62; array(\n				&#34;0601&#34; =&#62; &#34;鳥取県&#34;, &#34;0602&#34; =&#62; &#34;島根県&#34;, &#34;0603&#34; =&#62; &#34;岡山県&#34;, &#34;0604&#34; =&#62; &#34;広島県&#34;, &#34;0605&#34; =&#62; &#34;山口県&#34;, &#34;0606&#34; =&#62; &#34;徳島県&#34;, &#34;0607&#34; =&#62; &#34;香川県&#34;, &#34;0608&#34; =&#62; &#34;愛媛県&#34;, &#34;0609&#34; =&#62; &#34;高知県&#34;\n			)\n		),\n	&#34;07&#34; =&#62; array( &#34;tag&#34; =&#62; &#34;kyushu&#34;, &#34;name&#34; =&#62; &#34;九州地方&#34;,\n			&#34;pref&#34; =&#62; array(\n				&#34;0701&#34; =&#62; &#34;福岡県&#34;, &#34;0702&#34; =&#62; &#34;佐賀県&#34;, &#34;0703&#34; =&#62; &#34;長崎県&#34;, &#34;0704&#34; =&#62; &#34;熊本県&#34;, &#34;0705&#34; =&#62; &#34;大分県&#34;, &#34;0706&#34; =&#62; &#34;宮崎県&#34;, &#34;0707&#34; =&#62; &#34;鹿児島県&#34;\n			)\n		),\n	&#34;08&#34; =&#62; array( &#34;tag&#34; =&#62; &#34;okinawa&#34;, &#34;name&#34; =&#62; &#34;沖縄地方&#34;,\n			&#34;pref&#34; =&#62; array(\n				&#34;0801&#34; =&#62; &#34;沖縄県&#34;\n			)\n		)\n);\n\nforeach( $listPref as $area ) {\n?&#62;\n&#60;div class=&#34;area martop50&#34; id=&#34;&#60;?php echo $area[&#34;tag&#34;]; ?&#62;&#34;&#62;&#60;img src=&#34;&#60;?php bloginfo(&#039;template_url&#039;); ?&#62;/images/shop/tab_&#60;?php echo $area[&#34;tag&#34;]; ?&#62;.gif&#34; width=&#34;580&#34; height=&#34;30&#34; alt=&#34;&#60;?php echo $area[&#34;name&#34;]; ?&#62;&#34; /&#62;&#60;/div&#62;\n&#60;?php\n	foreach( $area[&#34;pref&#34;] as $presid =&#62; $prefname ) {\n		//各都道府県の情報を取得\n		$posts = get_posts(array(\n			&#039;numberposts&#039; =&#62; 10,\n			&#039;post_type&#039; =&#62; &#039;shop&#039;,\n			&#039;orderby&#039; =&#62; &#039;meta_value&#039;,\n			&#039;meta_key&#039; =&#62; &#039;pref&#039;,\n			&#039;order&#039; =&#62; &#039;ASC&#039;,\n			&#039;post_status&#039; =&#62; &#039;publish&#039;,\n			&#039;meta_query&#039; =&#62; array(\n				array(\n					&#039;key&#039; =&#62; &#039;pref&#039;,\n					&#039;value&#039; =&#62; $presid,  //都道府県ごとのIDが入る\n				)\n			)\n		));\n\n		if($posts){\n			//各都道府県の情報を出力する\n			echo &#039;&#60;div class=&#34;pref&#34;&#62;&#039; . $prefname . &#039;&#60;/div&#62;&#039;;\n			foreach($posts as $post){\n				echo &#039;&#60;h3&#62;&#60;a href=&#34;&#039; . get_permalink($post-&#62;ID) . &#039;&#34;&#62;&#039; . get_the_title($post-&#62;ID) . &#039;&#60;/a&#62;&#60;/h3&#62;&#039;;\n				echo &#039;&#60;div class=&#34;box&#34;&#62;&#039;;\n				if(get_field(&#039;main_image&#039;)) {\n					echo &#039;&#60;p class=&#34;alignleft&#34;&#62;&#039;;\n					$attachment_id = get_field(&#039;main_image&#039;);\n					$size = &#34;thumbnail&#34;; // (thumbnail, medium, large, full or custom size)\n					$image = wp_get_attachment_image_src( $attachment_id, $size );\n					$attachment = get_post( get_field(&#039;main_image&#039;) );\n					$alt = get_post_meta($attachment-&#62;ID, &#039;_wp_attachment_image_alt&#039;, true);\n					$image_title = $attachment-&#62;post_title;\n\n					echo &#039;&#60;a href=&#34;&#039;;\n					echo get_permalink($post-&#62;ID);\n					echo &#039;&#34;&#62;&#60;img src=&#34;&#039;;\n					echo $image[0];\n					echo &#039;&#34; width=&#34;&#039;;\n					echo $image[1];\n					echo &#039;&#34; height=&#34;&#039;;\n					echo $image[2];\n					echo &#039;&#34; alt=&#34;&#039;;\n					echo $alt;\n					echo &#039;&#34; title=&#34;&#039;;\n					echo $image_title;\n					echo &#039;&#34; class=&#34;main_img&#34; /&#62;&#039;;\n					echo &#039;&#60;/a&#62;&#60;/p&#62;&#039;;\n				}else{\n				}\n				echo &#039;&#60;ul&#62;&#039;;\n				if(get_field(&#039;zipcode&#039;)) {\n					echo &#039;&#60;li&#62;〒&#039;;\n					the_field(&#039;zipcode&#039;);\n					echo &#039;&#60;/li&#62;&#039;;\n				}else{\n				}\n				if(get_field(&#039;address&#039;)) {\n					echo &#039;&#60;li&#62;&#039;;\n					the_field(&#039;address&#039;);\n					echo &#039;&#60;br /&#62;&#039;;\n					the_field(&#039;building&#039;);\n					echo &#039;&#60;/li&#62;&#039;;\n				}else{\n				}\n				if(get_field(&#039;shop_tel&#039;)) {\n					echo &#039;&#60;li&#62;TEL：&#039;;\n					the_field(&#039;shop_tel&#039;);\n					if(get_field(&#039;shop_tel_type&#039;)==&#039;chokutsu&#039;) {\n						echo &#039;（直通）&#039;;\n					} elseif(get_field(&#039;shop_tel_type&#039;)==&#039;daihyo&#039;){\n						echo &#039;（代）&#039;;\n					}else{\n					}\n					echo &#039;&#60;/li&#62;&#039;;\n				}else{\n				}\n				echo &#039;&#60;/ul&#62;&#039;;\n				echo &#039;&#60;/div&#62;&#039;;\n			}\n		} else {\n		}\n	}\n}\n?&#62;</code></pre>\n<p>これで上手く表示できました。<br />\nただ、店舗がその地域に1店舗もない場合、地域名だけが表示されてしまいます。<br />\nif文でどうにかできるかとおもったのですが、上手くできませんでした。</p>\n<p>最悪、店舗がない地域の配列をコメントアウトすることも考えていますが、店舗の増減が激しく、スマートではないかとは思います。<br />\n申し訳ございませんが、お知恵を拝借できませんでしょうか。<br />\n何卒よろしくお願い申し上げます。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:8:"ibis7788";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:24;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:100:"kazuyoung :  &quot;固定ページの記事一覧で同じ投稿者が表示されてしまう&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120880#post-168413";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 02:11:51 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168413@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:2500:"<p>固定ページで複数の投稿者が書いている記事の一覧を出してるのですが、<br />\nそのループ内で表示されている投稿者が、記事の投稿者ではなく、<br />\n固定ページの投稿者になってしまいます。（つまり全員同じ投稿者になってしまいます）</p>\n<p>これを実際の記事の投稿者に変えることはできるのでしょうか。。。？</p>\n<p>大変困っています。<br />\n助けてください。よろしくお願いいたします。</p>\n<pre><code>&#60;?php\nglobal $post;\n$tmp_post = $post;\n$category_ids = array( &#039;2&#039; ); //カテゴリーIDを指定\n$numberposts = &#039;&#039;; //一覧表示したい記事数を数字で指定\nforeach ( $category_ids as $category_id ) {\n?&#62;\n  &#60;?php\n     $postslist = get_posts( &#34;category=$category_id&#38;numberposts=$numberposts&#38;order=DESC&#38;orderby=date&#38;post_not_in=array(132,173,177,179,181)&#34; );\n     foreach ( $postslist as $post ) {\n?&#62;\n&#60;?php echo get_template_part(&#039;content-archive&#039;); ?&#62;\n&#60;?php\n     }\n?&#62;\n&#60;?php\n }\n $post = $tmp_post;\n ?&#62;</code></pre>\n<p>上記の「content-archive.php」内は以下<br />\n<pre><code>&#60;article&#62;\n&#60;a href=&#34;&#60;?php the_permalink(); ?&#62;&#34;&#62;\n&#60;dl class=&#34;clearfix&#34;&#62;\n&#60;dt&#62;&#60;?php the_post_thumbnail(&#039;author_thumbnail&#039;, array(&#039;alt&#039; =&#62; the_title_attribute(&#039;echo=0&#039;), &#039;title&#039; =&#62; the_title_attribute(&#039;echo=0&#039;))); ?&#62;&#60;/dt&#62;\n&#60;dd&#62;&#60;time datetime=&#34;&#60;?php the_time(&#039;Y-m-d&#039;); ?&#62;&#34; pubdate=&#34;pubdate&#34;&#62;&#60;?php the_time(&#039;Y&#039;) ?&#62;.&#60;?php the_time(&#039;m&#039;) ?&#62;.&#60;?php the_time(&#039;d&#039;) ?&#62;(&#60;?php the_time(&#039;D&#039;) ?&#62;)&#60;/time&#62;&#60;/dd&#62;\n&#60;dd&#62;&#60;h3&#62;&#60;?php if (strlen($post-&#62;post_title) &#62; 35) {\necho mb_substr(the_title($before = &#039;&#039;, $after = &#039;&#039;, FALSE), 0, 35) . &#039;...&#039;; } else {\nthe_title(); } ?&#62;&#60;/h3&#62;&#60;/dd&#62;\n&#60;dd&#62;&#60;span&#62;著者：&#60;?php the_author(); ?&#62;&#60;/span&#62;&#60;/dd&#62;\n&#60;/dl&#62;\n\n&#60;ul class=&#34;category&#34;&#62;\n&#60;li&#62;&#60;?php if(function_exists(&#039;get_cat_icon&#039;)){get_cat_icon(&#039;link=false&#039;);}?&#62;&#60;/li&#62;\n&#60;?php setExpertsTag(); ?&#62;\n&#60;/ul&#62;\n\n&#60;/a&#62;\n&#60;/article&#62;</code></pre>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:9:"kazuyoung";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:25;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:70:"gatespace :  &quot;Google XML Sitemapsのsitemap.xmlに関して&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120061#post-168408";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 01:34:07 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168408@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:847:"<blockquote><p>webmastertoolに登録する際には、「sitemap.xml」の記述だけで問題ないのでしょうか。\n</p></blockquote>\n<p>プラグインの生成している .xml のサイトマップはGoogleの仕様に合っていますし、<br />\n少なくとも自分の管理するサイトでは問題ありません。<br />\n（Webマスターツールで確認済み）</p>\n<p>Webマスターツールでサイトマップの「テスト」が出来ますが、それはやってみたんですか？<br />\nテストで問題なければ</p>\n<blockquote><p>サイトマップ: /sitemap.xml<br />\nタイプ: サイトマップ インデックス<br />\nサイトマップ インデックス内の子の数 26<br />\nエラーの詳細: エラーは見つかりませんでした。\n</p></blockquote>\n<p>となるはずです。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:9:"gatespace";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:26;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:79:"sorarosky :  &quot;カスタムフィールド値での絞り込み検索&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120876#post-168407";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 01:31:37 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168407@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:2279:"<p>テーマを構築しながらPHPの勉強中です。</p>\n<p><a href="http://www.dancealive.tv/dictionarytop" rel="nofollow">http://www.dancealive.tv/dictionarytop</a><br />\nこちらのサイトで実装されているような、チェックボックスを使用した複数のカスタムフィールド値での絞り込み検索を実現したくて、苦戦しています。</p>\n<p><a href="http://satoru-xyon.blogspot.jp/2013/02/wordpress.html" rel="nofollow">http://satoru-xyon.blogspot.jp/2013/02/wordpress.html</a><br />\nこちらのサイトで紹介されているように、フォーム等をアーカイブページに設置したのですが、<br />\nその他にアーカイブページや、search.phpにどのような記述をすれば動作するのかが分かりません。</p>\n<p>以下アーカイブページの記述</p>\n<p>投稿カテゴリー：results<br />\nカスタムフィールド：type、kind</p>\n<p>&#60;form role="search" method="get" action="&#60;?php echo home_url(); ?&#62;/results"&#62;<br />\n&#60;input type="checkbox" name="type[]" value="中華" &#62; 中華<br />\n&#60;input type="checkbox" name="type[]" value="イタリアン" &#62; イタリアン<br />\n&#60;input type="checkbox" name="kind[]" value="インクジェットタイプ" &#62; インクジェットタイプ<br />\n&#60;input type="checkbox" name="kind[]" value="クロスタイプ" &#62; クロスタイプ<br />\n&#60;input type="submit" value="SEARCH" /&#62;<br />\n&#60;/form&#62;<br />\n&#60;?php<br />\n$type = $_GET[''type''];<br />\n$kind = $_GET[''kind''];</p>\n<p>query_posts( array(<br />\n  ''post_type'' =&#62; ''post'' ,<br />\n  ''posts_per_page'' =&#62; 30 ,<br />\n  ''order'' =&#62; ASC,<br />\n  ''orderby'' =&#62; title ,<br />\n  ''paged'' =&#62; get_query_var( ''paged'' ),<br />\n  ''meta_query'' =&#62; array(<br />\n   array( ''key''=&#62;''type'',<br />\n    ''value''=&#62;$type,<br />\n    ''compare''=&#62;''IN''<br />\n    ),<br />\n   array( ''key''=&#62;''kind'',<br />\n    ''value''=&#62;$kind,<br />\n    ''compare''=&#62;''IN'',<br />\n    ),<br />\n   ''relation''=&#62;''AND''<br />\n   )<br />\n  )<br />\n );<br />\n?&#62;</p>\n<p>分かりにくい質問かもしれませんが、どなたかご教授いただければ幸いです。<br />\n何卒よろしくお願い致します。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:9:"sorarosky";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:27;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:90:"tekro :  &quot;画像の相対パスが変わったのでリダイレクトしたい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120875#post-168406";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 01:30:49 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168406@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:767:"<p>サーバーを移転した際に、Wordpressアドレスが「http://example.com/」から「http://example.com/wordpress」に変わり、画像ファイルのアドレス（パス）が全部変わってしまいました。</p>\n<p>すでにGoogleにインデックスされた画像の旧アドレスから新しいアドレスに301リダイレクトしたいのですが、どういう方法をとれば良いのでしょうか。</p>\n<p>「Simple 301 Redirects」というプラグインで以下のように設定しましたが、リダイレクトされませんでした。</p>\n<p>「/wp-content/uploads/」　→　「/wordpreess/wp-content/uploads/」</p>\n<p>なんとか上記の設定を反映させたいです。宜しくお願いします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:5:"tekro";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:28;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:64:"nobita :  &quot;空白を削除したいのですが……&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/119552#post-168405";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 00:50:00 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168405@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1674:"<blockquote><p>もう少しスマートな方法がないのかなぁと難儀しています。</p></blockquote>\n<p>頑張ってください。</p>\n<p>ソースを見てみましたが、</p>\n<pre><code>&#60;div id=&#34;recent-posts-2&#34; class=&#34;widget widget_recent_entries&#34;&#62;\n&#60;h3 class=&#34;widgettitle&#34;&#62;最近の投稿&#60;/h3&#62;\n&#60;ul&#62;\n&#60;li&#62;&#60;a href=&#34;http://wanderer.netgamers.jp/2014/04/29/214744/&#34;&#62;見るのとやるのは違うのだよ &#60;span class=&#34;post-date&#34;&#62;(04月29日)&#60;/span&#62;&#60;/li&#62;&#60;/a&#62;\n&#60;li&#62;&#60;a href=&#34;http://wanderer.netgamers.jp/2014/04/28/222431/&#34;&#62;そしてBランク、まだプレイ時間は1時間強 &#60;span class=&#34;post-date&#34;&#62;(04月28日)&#60;/span&#62;&#60;/li&#62;&#60;/a&#62;\n&#60;li&#62;&#60;a href=&#34;http://wanderer.netgamers.jp/2014/04/27/211511/&#34;&#62;MGSV：GZクリア。但しC評価。 &#60;span class=&#34;post-date&#34;&#62;(04月27日)&#60;/span&#62;&#60;/li&#62;&#60;/a&#62;\n&#60;li&#62;&#60;a href=&#34;http://wanderer.netgamers.jp/2014/04/20/063631/&#34;&#62;まだ販売中止なのかorz &#60;span class=&#34;post-date&#34;&#62;(04月20日)&#60;/span&#62;&#60;/li&#62;&#60;/a&#62;\n&#60;li&#62;&#60;a href=&#34;http://wanderer.netgamers.jp/2014/04/19/204618/&#34;&#62;まだPS3版が進んでいないのに&#38;hellip;&#38;hellip;（汗 &#60;span class=&#34;post-date&#34;&#62;(04月19日)&#60;/span&#62;&#60;/li&#62;&#60;/a&#62;\n&#60;/ul&#62;\n&#60;/div&#62;</code></pre>\n<p>a要素の閉じタグが、閉じliの後ろに来ているので、直しておいたほうがいいと思います。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"nobita";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:29;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:75:"keziyajones :  &quot;画像枠、ボックスシャドウの消し方&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120874#post-168404";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 00:47:50 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168404@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:558:"<p>Catch Evolutionというテーマを使用しています。<br />\n投稿した画像に角丸の枠線（box shadow付き）が強制的に付いてきます。<br />\nこれを表示しないで純粋に画像のみを表示させる方法はございますでしょうか？</p>\n<p>テーマに用意されているサポートフォーラム（英語サイト）などを調べ、同じ課題を抱えている方の対処方法を片っ端からテストしてみましたが、改善されていません。</p>\n<p>ぜひご教示願います。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:11:"keziyajones";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:30;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:86:"nobita :  &quot;raindrops 自動挿入されるp、brタグを削除する方法&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120362#post-168403";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 30 Apr 2014 00:18:27 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168403@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1405:"<p>こんにちは</p>\n<p>ページは、htmlで入力するので、余計なp要素の追加はいらないといいうことだと思いますが、</p>\n<p>the_content hookは、Raindropsでも利用できます。<br />\nthe_contentに相当する関数は、<code>raindrops_entry_content()</code>になります。</p>\n<p>提示された<br />\n<pre><code>remove_filter( &#039;the_content&#039;, &#039;wpautop&#039;);</code></pre>\n<p>は、そのままでは、twentyfourteen raindrops ともに動作しないようでした。<br />\n（　細かくは、追っかけていません　）</p>\n<p>代替案<br />\n<pre><code>&#60;?php\nadd_filter( &#039;the_content&#039;, &#039;wpautop2&#039;, 5 );\nadd_filter( &#039;the_content&#039;, &#039;wpautop3&#039;, 11 );\n\nfunction wpautop2( $content ) {\n\n    if ( is_page() ) {\n\n        return str_replace( &#34;\\n&#34;, &#039;&#039;, $content );\n    }\n}\n\nfunction wpautop3( $content ) {\n    if ( is_page() ) {\n        return str_replace( array( &#34;&#60;p&#62;&#34;, &#34;&#60;/p&#62;&#34; ), &#039;&#039;, $content );\n    }\n}</code></pre>\n<p>ただし、このフィルタだと、投稿本文のすべての改行を除去します。</p>\n<p>余</p>\n<p>WordPress3.9になって、P要素のおかしな動作は、だいぶ改善されたみたいですので、（tdやdivの内側やoption要素あたり）一応チェックをお勧めします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"nobita";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:31;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:28:"digit@maetel :  &quot;&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120817#post-168365";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 29 Apr 2014 19:51:46 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168365@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1468:"<blockquote><p>投稿に記載する内容</p>\n<p>どのような条件でどのような問題が見られますか? 以下のポイントを考慮して、正確かつ具体的に記述してください。</p>\n<p>WordPress のバージョン<br />\n使用しているプラグインとテーマの情報(名称、入手先 URL、バージョン)<br />\nPHP、MySQL のバージョン<br />\nサーバー環境(ホスティングサービス名、OS、ウェブサーバー等)<br />\nブラウザとそのバージョン<br />\nエラーが表示された場合、「XX のようなエラー」ではなく、なるべくエラー文をコピーしてそのまま貼り付けてください</p></blockquote>\n<p><a href="http://ja.forums.wordpress.org/topic/11794?replies=1">問題解決のためのチェックリスト</a></p>\n<p>WordPressのバージョン<br />\n使用プラグインがわからないのでなんとも予測つかないのですが、<br />\nプラグインが災いしているのなら、全無効にして同じ現象が起こる組み合わせを突き止めましょう。<br />\n例えば「Ultimate Tinymce」プラグインのオプションで<br />\n「エディターのスクロールバー位置を保存」にチェックが入ってませんか。</p>\n<p>それでも起こるのなら、<br />\n「<a href="http://codex.wordpress.org/WP_DEBUG">デバッグモード</a>」で確認すると問題点が追求しやすいですよ。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"digit@maetel";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:32;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:30:"smoked_painful :  &quot;&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120817#post-168345";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 29 Apr 2014 17:55:33 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168345@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1036:"<p>私は、レンタルサーバー利用者で、壊れると怖いのでシステムのバージョンアップ（更新）をまだ一回も出来ておりません。もしかしたら、このせいでしょうか？</p>\n<p>さて、グレーになった画面というのは、背景の空白部分のようです。垂直のスライドバー位置をみると、一番下の位置まで降りてきておりました。でも、一画面でみれる内容の時でもです。（おかしいな。）<br />\n手動でスライドバーを一番上まで持ち上げると、編集画面が再び出ます。しかし、マウスボタンキーを離すと、すぐ自動的に下まで戻っていって元の木阿弥です。（イメージ的には、何か嫌がらせをされている感じですね。）</p>\n<p>新規投稿時にはあまり起こりませんが、一回でも投稿ボタンか改行でのエンターキーを押すと、この現象が頻発して出てきます。どうしてでしょうか？\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:14:"smoked_painful";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:33;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:61:"Takuro Hishikawa :  &quot;WP-PageNavi　の記述場所&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120060#post-168309";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 29 Apr 2014 14:29:15 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168309@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:489:"<p>新着記事は<code>get_posts()</code>で取得していますね。<code>get_posts()</code>を使うと、<code>wp_pagenavi()</code>を記述しても、<code>get_posts()</code>で表示している一覧についてのページ送りになりません。ただしくページ送りを表示するには、私が上述したコードのように、<code>wp_pagenavi()</code>の引数<code>query</code>に<code>WP_Query</code>のインスタンスを渡す必要があります。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:16:"Takuro Hishikawa";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:34;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:77:"Takuro Hishikawa :  &quot;Google XML Sitemapsのsitemap.xmlに関して&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"http://ja.forums.wordpress.org/topic/120061#post-168307";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 29 Apr 2014 14:26:20 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:38:"168307@http://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:140:"<p>たぶん、反映されないと思いますが、Googleの仕様についてはGoogleのフォーラムでお尋ねください。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:16:"Takuro Hishikawa";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}s:27:"http://www.w3.org/2005/Atom";a:1:{s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:3:{s:4:"href";s:35:"http://ja.forums.wordpress.org/rss/";s:3:"rel";s:4:"self";s:4:"type";s:19:"application/rss+xml";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}}}}}}s:4:"type";i:128;s:7:"headers";a:6:{s:6:"server";s:5:"nginx";s:4:"date";s:29:"Wed, 30 Apr 2014 06:50:54 GMT";s:12:"content-type";s:23:"text/xml; charset=UTF-8";s:10:"connection";s:5:"close";s:4:"vary";s:15:"Accept-Encoding";s:13:"last-modified";s:29:"Wed, 30 Apr 2014 06:49:41 GMT";}s:5:"build";s:14:"20130910200210";}', 'no');
